import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Login.module.css';
import logoSeems from '../assets/images/logo_seems.png';
import naverIcon from '../assets/images/naver.png';
import kakaoIcon from '../assets/images/kakao.png';
import faceioIcon from '../assets/images/faceio.png';
import ApiTest from '../components/common/ApiTest';
import { AuthContext } from '../AuthProvider';
import apiClient from '../utils/axios';


function Login({onLoginSuccess}) {
  const navigate = useNavigate(); // 페이지 이동을 위한 useNavigate 훅 사용

  const [userId, setUserid] = useState('');
  const [userPwd, setUserpwd] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // AutoProvider 에서 가져온 updateTokens 함수 사용 선언함
  const { updateTokens } = useContext(AuthContext);

  // 이미 로그인 상태라면 메인 페이지로 리다이렉트
  useEffect(() => {
    const accessToken = localStorage.getItem('accessToken');
    if (accessToken) navigate('/');
  }, [navigate]);

  // Base64 디코딩 함수 추가
  const base64DecodeUnicode = (base64Url) => {
    try {
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(
        atob(base64)
          .split('')
          .map((c) => `%${('00' + c.charCodeAt(0).toString(16)).slice(-2)}`)
          // 빽틱 (`) 사용할 것
          .join('')
      );
      return JSON.parse(jsonPayload); // 페이로드 문자열을 json 객체로 파싱해서 리턴함
    } catch (error) {
      console.error('JWT 디코딩 실패 : ', error);
      return null;
    }
  };

  // enter 키 누르면 로그인 실행 처리 함수 추가
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

    // 로그인 처리 함수 추가
  const handleLogin = async () => {
    // 중복 요청 방지
    if (isLoggedIn) return;

    setIsLoggedIn(true);
    try {
      const response = await apiClient.post('/login', {
        userId: userId,
        userPwd: userPwd,
      });

      console.log('서버 응답 데이터 : ', response);

      // response body 에 있는 정보 추출 (서버측에서 저장한 이름(key)과 일치시킴)
      const { accessToken, refreshToken, userId, userName, role } =
        response.data;

      // JWT accessToken 디코딩
      const tokenPayload = base64DecodeUnicode(accessToken.split('.')[1]);
      if (!tokenPayload) {
        console.error('JWT 페이로드 디코딩 실패.');
        throw new Error('JWT 페이로드 디코딩 실패'); //catch 로 넘어가게 함
      }

      console.log('JWT 페이로드 : ', tokenPayload);

      try {
        updateTokens(accessToken, refreshToken); // 전역 상태 관리 업데이트
        console.log('로컬스토리지 저장 성공.');
        console.log('AuthContext 에 로그인 상태 정보 업데이트 성공.');
      } catch (storageError) {
        console.error(
          '로컬 스토리지 저장 실패 또는 전역 상태 업데이트 실패.',
          storageError
        );
        throw storageError; // 바깥 catch 로 넘김
      }

      //alert('로그인 성공.');
      //로그인 성공시 부모 컴포넌트로 알림
      if (onLoginSuccess) onLoginSuccess();
    } catch (error) {
      console.error('로그인 실패 : ', error);

      if (error.response) {
        console.error('서버측 에러 응답 데이터 : ', error.response.data);
        alert(error.response.data.error || '서버 오류로 인해 로그인 실패!');
      } else if (error instanceof Error) {
        console.error('에러 메세지 :', error.message);
        alert(error.message);
      } else {
        console.error('예상치 못한 오류 : ', error);
        alert('알 수 없는 오류 발생.');
      }
    } finally {
      setIsLoggedIn(false);
    }
  };



  // const handleChange = (e) => {
  //   const { name, value } = e.target;
  //   setFormData(prev => ({
  //     ...prev,
  //     [name]: value
  //   }));
  // };


  const handleSocialLogin = (provider) => {
    // 소셜 로그인 로직 구현
    console.log(`${provider} 로그인 시도`);
  };

  const handleSignupClick = () => {
    // 회원가입 페이지 이동
    console.log('회원가입 페이지로 이동');
    navigate('/signup');
  };

  const handlePwFindClick = () => {
    // 비밀번호 찾기 페이지 이동
    console.log('비밀번호 찾기 페이지로 이동');
    navigate('/pwfind');
  };

  return (
    <div className={styles.loginContainer}>
      <div className={styles.logoWrap}>
        <div className={styles.logoText}>
          <span>SEE</span>
          <span>MS</span>
        </div>
        <img 
          src={logoSeems} 
          alt="SEE MS 로고" 
          className={styles.logoImage}
        />
      </div>
      
      <div className={styles.loginTitle}>로그인</div>
      
      <form className={styles.loginForm}>
        <label htmlFor="userId">아이디</label>
        <input
          type="text"
          id="id"
          name="id"
          placeholder="아이디를 입력하세요"
          value={userId}
          onChange={(e) => setUserid(e.target.value)}
          required
        />
        
        <label htmlFor="userPwd">비밀번호</label>
        <input
          type="password"
          id="password"
          name="password"
          placeholder="비밀번호를 입력하세요"
          value={userPwd}
          onChange={(e) => setUserpwd(e.target.value)}
          onKeyDown={handleKeyDown}
          required
        />
        
        <div className={styles.forgotPassword}>
          <span 
          onClick={() => handlePwFindClick()} 
          style={{ cursor: 'pointer', color: '#4b94d0', fontWeight: '900', textDecoration: 'none' }}
          >비밀번호를 잊으셨나요?
          </span>
        </div>
        
        <button type="submit" className={styles.loginBtn} onClick={handleLogin}>
          로그인
        </button>
      </form>
      
      <div className={styles.signupLink}>
        아직 회원이 아니신가요?
        <span 
          onClick={() => handleSignupClick()} 
          style={{ cursor: 'pointer', color: '#4b94d0', fontWeight: '900', textDecoration: 'none' }}
        >{'  '}
        회원가입
        </span>
      </div>
      
      <div className={styles.divider}>
        <hr />
        <span>또는</span>
        <hr />
      </div>
      
      <div className={styles.socialLogin}>
        <button 
          className={`${styles.socialBtn} ${styles.google}`}
          onClick={() => handleSocialLogin('google')}
        >
          <img 
            src="https://img.icons8.com/color/24/000000/google-logo.png" 
            alt="구글 로고"
            className={styles.socialIcon}
          />
          구글 로그인
        </button>
        
        <button 
          className={`${styles.socialBtn} ${styles.naver}`}
          onClick={() => handleSocialLogin('naver')}
        >
          <img 
            src={naverIcon} 
            alt="네이버 로고"
            className={styles.socialIcon}
          />
          네이버 로그인
        </button>
        
        <button 
          className={`${styles.socialBtn} ${styles.kakao}`}
          onClick={() => handleSocialLogin('kakao')}
        >
          <img 
            src={kakaoIcon} 
            alt="카카오 로고"
            className={styles.socialIcon}
          />
          카카오 로그인
        </button>
        
        <button 
          className={`${styles.socialBtn} ${styles.faceio}`}
          onClick={() => handleSocialLogin('faceio')}
        >
          <img 
            src={faceioIcon} 
            alt="페이스 아이콘"
            className={styles.socialIcon}
          />
          페이스 로그인
        </button>
      </div>
      
      {/* API 연동 테스트 컴포넌트 */}
      <ApiTest />
    </div>
  );
};

export default Login;
